sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

var user = {
  name: "John",
  age: 30,
  isAdmin: true
};
for (var key in user) {
  // keys and values for the keys
  gs.info("#ME : key= " + key + "user[key] = " + user[key] ); 
}

gs.info("Test1 " + (new GlideDateTime()).getLocalTime().getByFormat('hh:mm:ss a'));
gs.info("Test1.2 " + (new GlideDateTime()).getLocalDate().getByFormat('yyyy:MM:dd'));

// Query the database for NeedIt Task records with Due date field values within
// 24 hours from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var dueSoon = new GlideRecord('x_58872_needit_needit_task');
dueSoon.addQuery('due_date','>=',rightNow);
dueSoon.addQuery('due_date','<',gs.hoursAgo(-24));
dueSoon.addQuery('state','!=',3);

dueSoon.query();



gs.info("Test2 gs.hoursAgo(-24) = " + gs.hoursAgo(-24));

if (dueSoon.getRowCount() == 0) {
	gs.info("#ME :   no record found!");
}
// Write a log message for each NeedIt Task Record due within 24 hours from now
while(dueSoon.next()){
	gs.info("#ME :   NeedIt Task due SOON  = " + dueSoon.number + "; short description = " + dueSoon.short_description);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', dueSoon, dueSoon.number, gs.getUserName());
}
